import React from "react";
import './admin-css/add-project.css';
import {useState} from "react";
import axios from "axios";
import "./admin-css/popup.css";

export default function Add_project() {
    const[title, setTitle] = useState('');
    const[bio, setBio] = useState('');
    const[descripition, setDescription] = useState('');
    const[github_link, setGithub_link] = useState('');
    const[image, setImage] = useState(null);
    const [popup, setPopup] = useState(null);
    const close = () => {
        setPopup(null);
    }

    function Popup({message, status}) {
        return(
            <>
                <div className="popup-body">
                    <div className="popup-content">
                        <div className="popup-header">
                            <a onClick={close}>
                                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><path fill="#c34600" d="M256 512A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM175 175c9.4-9.4 24.6-9.4 33.9 0l47 47 47-47c9.4-9.4 24.6-9.4 33.9 0s9.4 24.6 0 33.9l-47 47 47 47c9.4 9.4 9.4 24.6 0 33.9s-24.6 9.4-33.9 0l-47-47-47 47c-9.4 9.4-24.6 9.4-33.9 0s-9.4-24.6 0-33.9l47-47-47-47c-9.4-9.4-9.4-24.6 0-33.9z"/></svg>
                            </a>
                        </div>
                        <div className="popup-main">
                            <div className="popup-box">
                            {
                                status == true ? (
                                    <a>
                                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><path fill="#00ff00" d="M256 512A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM369 209L241 337c-9.4 9.4-24.6 9.4-33.9 0l-64-64c-9.4-9.4-9.4-24.6 0-33.9s24.6-9.4 33.9 0l47 47L335 175c9.4-9.4 24.6-9.4 33.9 0s9.4 24.6 0 33.9z"/></svg>
                                    </a>
                                ) : 
                                    <a>
                                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"><path fill="#ff0000" d="M256 512A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM175 175c9.4-9.4 24.6-9.4 33.9 0l47 47 47-47c9.4-9.4 24.6-9.4 33.9 0s9.4 24.6 0 33.9l-47 47 47 47c9.4 9.4 9.4 24.6 0 33.9s-24.6 9.4-33.9 0l-47-47-47 47c-9.4 9.4-24.6 9.4-33.9 0s-9.4-24.6 0-33.9l47-47-47-47c-9.4-9.4-9.4-24.6 0-33.9z"/></svg>
                                    </a>
                            }
                                <p>{message}</p>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        );
    }

    const submit = async (e) => {
        e.preventDefault();
        const formdata = new FormData();
        formdata.append('title', title);
        formdata.append('bio', bio);
        formdata.append('descripition', descripition);
        formdata.append('github_link', github_link);
        formdata.append('image', image);
        try {
            const result = await axios.post('http://localhost:3001/createProject', formdata, {
                headers: {
                    "Content-Type" : "multipart/form-data"
                }
            })
            setPopup(<Popup
                status ={true}
                message={"Succesfully Added to the server"}
                closepopup={close}
            />);
            console.log(result);
        } catch (err) {
            setPopup(<Popup
                status ={false}
                message={"Inserting Data Failed"}
                closepopup={close}
            />);
            console.log(err);
        }
    }

    return(
        <>
            {popup}  
            <div className="add-project-body">
                <div className="project_content">
                    <h2>Add Project</h2>
                    <form onSubmit={submit} mult>
                        <input onChange={(e) => {setTitle(e.target.value)}} type="text" placeholder="Title" required/>
                        <input onChange={(e) => {setBio(e.target.value)}} type="text" placeholder="Bio" required/>
                        <textarea onChange={(e) => {setDescription(e.target.value)}} className="textarea" type="text" style={{height: '70px', borderRadius: '10px', width: "95%", marginLeft: "5px", marginTop: "5px"}} placeholder="Description" required/>
                        <input onChange={(e) => {setImage(e.target.files[0])}} type="file" placeholder="Upload Image" required/>
                        <input onChange={(e) => {setGithub_link(e.target.value)}} type="text" placeholder="Github Link" required/>
                        <button onClick={() => {
                            close()
                        }}>Submit</button>
                    </form>
                </div>
            </div>
        </>
    );
}
